import React, { useState } from 'react'

function SIgnup(){
  const [formData, setFormData] = useState({
    name: "",
    email:"",
    password: "",
  });

  function changeHandler(event){
    const {name , value} =event.target;
    setFormData(prevFormData => ({
      ...prevFormData,
      [name]: value
    }));
  }


  function submitHandler(event) {
    event.preventDefault();
    // Here, you can send `formData` to your backend
    console.log(formData);
  }

  return (
    <div className='w-max mx-auto border-2 flex flex-col items-center justify-center min-h-screen p-4'>
      <h1 className='text-2xl font-bold'>Sign Up</h1>
      <form onSubmit={submitHandler}>
        <div><input type="text" id="name" value={formData.name} name="name" required placeholder='Enter Your Name' className='w-[300px] h-[35px] outline outline-2 outline-slate-300 rounded-sm mb-4 mt-4' onChange={changeHandler}/></div>
        <div><input type="email" id="email" value={formData.email} name="email" required placeholder='Enter email' className='w-[300px] h-[35px] outline outline-2 outline-slate-300 rounded-sm mb-4 mt-4' onChange={changeHandler}/></div>
        <div><input type="password" id="password" value={formData.password} name="password" required placeholder='Create Password' className='w-[300px] h-[30px] outline outline-2 outline-slate-300 rounded-sm mb-4 mt-4' onChange={changeHandler}/></div>
        <div><button className=' w-[300px] h-[40px] bg-blue-600 text-white font-bold rounded py-2 px-4'>Register</button></div>
      </form>
    </div>
  )
}

export default SIgnup
