import React, { useState } from "react";
import axios from "axios";

function UploadResume({ userId }) {
  const [file, setFile] = useState(null);

  function handleFileChange(event) {
    setFile(event.target.files[0]);
  }

  async function handleSubmit(event) {
    event.preventDefault();

    if (!file) {
      alert("Please select a file");
      return;
    }

    const formData = new FormData();
    formData.append("resume", file);

    try {
      const response = await axios.post(`/user/upload/:${user.Id}`, formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });
      console.log("File uploaded successfully:", response.data);
      alert("Resume uploaded successfully.");
    } catch (error) {
      console.error("Error uploading file:", error.response.data);
      alert("Error uploading resume. Please try again.");
    }
  }
  return (
    <div className="w-max h-max border-2 flex flex-col self-center justify-self-center m-4 p-3">
      <h1 className="text-2xl font-bold">Upload Resume</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <input
            type="file"
            name="resume"
            accept=".pdf,.doc,.docx"
            onChange={handleFileChange}
            className="mb-4"
          />
        </div>
        <div>
          <button
            type="submit"
            className="w-[300px] h-[40px] bg-blue-600 text-white font-bold rounded py-2 px-4"
          >
            Upload
          </button>
        </div>
      </form>
    </div>
  );
}

export default UploadResume;
